import {
    Card,
    MenuItem,
    Divider,
    IconButton,
    Icon,
    Hidden,
    useMediaQuery,
} from '@mui/material'
import {
    MatxSidenavContainer,
    MatxSidenav,
    MatxSidenavContent,
} from 'app/components'
import React, { useState, useEffect } from 'react'
import { Box, styled, useTheme } from '@mui/system'
import { H5, H6, Paragraph } from 'app/components/Typography'

const FlexBox = styled(Box)(() => ({
    display: 'flex',
    alignItems: 'center',
}))

const SidebarCardRoot = styled('div')(({ theme }) => ({
    position: 'relative',
    '& .headerBG': {
        height: 200,
        background: theme.palette.primary.main,
        backgroundImage: "url('/assets/images/home-bg-black.png')",
        backgroundSize: 'contain',
    },
    '& .sidenavHolder': {
        marginTop: -200,
    },
    '& .sidenav': {
        background: 'transparent !important',
        [theme.breakpoints.down('sm')]: {
            background: theme.palette.background.default,
        },
    },
}))

const SideNavHeader = styled(H6)(({ theme }) => ({
    padding: '24px',
    paddingLeft: '36px',
    color: 'rgba(255,255,255,0.87) !important',
    [theme.breakpoints.down('sm')]: {
        color: 'inherit !important',
    },
}))

const LeftSidebarCard = () => {
    const [open, setOpen] = useState(true)
    const theme = useTheme()
    const bgDefault = theme.palette.background.default
    const isMobile = useMediaQuery(theme.breakpoints.down('sm'))

    const toggleSidenav = () => {
        setOpen(!open)
    }
    useEffect(() => {
        if (isMobile) setOpen(false)
        else setOpen(true)
    }, [isMobile])

    return (
        <SidebarCardRoot>
            <div className="headerBG" />
            <div className="sidenavHolder">
                <MatxSidenavContainer>
                    <MatxSidenav
                        width="320px"
                        bgClass="bg-transparent"
                        sx={{ background: 'transparent' }}
                        open={open}
                        toggleSidenav={toggleSidenav}
