import React, { useState, useEffect } from 'react'
import { Icon, IconButton, Hidden, useMediaQuery } from '@mui/material'
import { Box, styled, useTheme } from '@mui/system'
import {
    MatxSidenavContainer,
    MatxSidenav,
    MatxSidenavContent,
} from 'app/components'
import UserProfileContent from './UserProfileContent'
import UserProfileSidenav from './UserProfileSidenav'

const ProfileRoot = styled('div')(({ theme }) => ({
    position: 'relative',
    '& .headerBG': {
        height: 345,
        background: theme.palette.primary.main,
        '@media only screen and (max-width: 959px)': {
            height: 400,
        },
    },
}))

const UserProfile = () => {
    const theme = useTheme()
    const [open, setOpen] = useState(true)
    const isMobile = useMediaQuery(theme.breakpoints.down('sm'))

    const toggleSidenav = () => {
        setOpen(!open)
    }
    useEffect(() => {
        if (isMobile) setOpen(false)
        else setOpen(true)
    }, [isMobile])

    return (
        <ProfileRoot>
            <MatxSidenavContainer>
                <MatxSidenav
                    width="300px"
                    open={open}
                    toggleSidenav={toggleSidenav}
                >
                    <Box textAlign="right" className="headerBG">
                        <Hidden smUp>
                            <IconButton onClick={toggleSidenav}>
                                <Icon sx={{ color: '#fff' }}>clear</Icon>
                            </IconButton>
                        </Hidden>
                    </Box>
                    <UserProfileSidenav />
                </MatxSidenav>
                {/* <MatxSidenavContent open={open}>
                    <Box className="headerBG" />
                    <UserProfileContent toggleSidenav={toggleSidenav} />
                </MatxSidenavContent> */}
            </MatxSidenavContainer>
        </ProfileRoot>
    )
}

export default UserProfile
