import { authRoles } from 'app/auth/authRoles'

export const navigations = [
    {
        name: 'Dashboard',
        path: '/dashboard/default',
        icon: 'dashboard',
        auth: authRoles.sa // ONLY SUPER ADMIN(SA) CAN ACCESS
    },
    {
        name: 'Inventory Management',
        path: '/dashboard/inventory-management',
        icon: 'store',
    },
    {
        label: 'Pages',
        type: 'label',
    },

    {
        name: 'Forms',
        icon: 'description',

        children: [
            {
                name: 'Order Form',
                path: '/forms/order-form',
                iconText: 'OF',
            },
            {
                name: 'Invoice Form',
                path: '/forms/invoice-form',
                iconText: 'IF',
            },
            {
                name: 'Property Listing Form',
                path: '/forms/property-listing-form',
                iconText: 'PF',
            },
            {
                name: 'Basic',
                path: '/forms/basic',
                iconText: 'B',
            },
            {
                name: 'Upload',
                path: '/forms/upload',
                iconText: 'U',
            },
            {
                name: 'Wizard',
                path: '/forms/wizard',
                iconText: 'W',
            },
        ],
    },
    {
        name: 'Left Sidebar Card',
        path: '/page-layouts/Left-sidebar-card',
        icon: 'vertical_split',
    },
    {
        name: 'User Profile',
        path: '/page-layouts/user-profile',
        icon: 'person',
    },

    {
        label: 'Components',
        type: 'label',
    },

    {
        name: 'Map',
        icon: 'add_location',
        path: '/map',
    },
    {
        label: 'Charts',
        type: 'label',
    },
    {
        name: 'Charts',
        icon: 'trending_up',

        children: [
            {
                name: 'Echarts',
                path: '/charts/echarts',
                iconText: 'E',
            },
            {
                name: 'Recharts',
                path: '/charts/recharts',
                iconText: 'R',
            },
            {
                name: 'Apex Charts',
                path: '/charts/apex-charts',
                iconText: 'A',
            },
        ],
    },
    {
        name: 'Documentation',
        icon: 'launch',
        type: 'extLink',
        path: 'http://demos.ui-lib.com/matx-react-doc/',
    },
]

export const getfilteredNavigations = (navList = [], role) => {
    return navList.reduce((array, nav) => {
        if (nav.auth)
        {
            if (nav.auth.includes(role))
            {
                array.push(nav)
            }
        } else
        {
            if (nav.children)
            {
                nav.children = getfilteredNavigations(nav.children, role)
                array.push(nav)
            } else
            {
                array.push(nav)
            }
        }
        return array
    }, [])
}
